import React, { useState } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import styles from './inventory.module.css';
import itemConfig from '../items/itemConfig.json';

const ItemTypes = {
  ITEM: 'item',
};

function Inventory({ inventory = [], setPlayer, maxInventorySpace }) {
  const [hoveredItem, setHoveredItem] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);

  const handleMoveItem = (fromIndex, toIndex) => {
    if (fromIndex === toIndex) return;

    const updatedInventory = [...inventory];
    const [movedItem] = updatedInventory.splice(fromIndex, 1);
    updatedInventory.splice(toIndex, 0, movedItem);

    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      inventory: updatedInventory,
    }));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className={styles.inventoryContainer}>
        <div className={styles.layout}>
          {Array.from({ length: maxInventorySpace }).map((_, index) => (
            <InventorySlot
              key={index}
              index={index}
              item={inventory[index]}
              moveItem={handleMoveItem}
              hoveredItem={hoveredItem}
              setHoveredItem={setHoveredItem}
              selectedItem={selectedItem}
              setSelectedItem={setSelectedItem}
            />
          ))}
        </div>
        {hoveredItem && hoveredItem.itemData && (
          <div className={styles.tooltip} style={hoveredItem.position}>
            <p>
              <strong>{hoveredItem.itemData.name}</strong>
            </p>
            <p>{hoveredItem.itemData.description}</p>
            {hoveredItem.itemData.attackBoost && (
              <p>Attack Boost: {hoveredItem.itemData.attackBoost}</p>
            )}
            {hoveredItem.itemData.defenceBoost && (
              <p>Defence Boost: {hoveredItem.itemData.defenceBoost}</p>
            )}
            {hoveredItem.itemData.effect?.heal && (
              <p>Heal: {hoveredItem.itemData.effect.heal}</p>
            )}
          </div>
        )}
      </div>
    </DndProvider>
  );
}

function InventorySlot({ index, item, moveItem, hoveredItem, setHoveredItem, selectedItem, setSelectedItem }) {
  const [, ref] = useDrop({
    accept: ItemTypes.ITEM,
    hover: (draggedItem) => {
      if (draggedItem.index !== index) {
        moveItem(draggedItem.index, index);
        draggedItem.index = index;
      }
    },
  });

  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.ITEM,
    item: { index },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const itemData = item ? itemConfig.items.find((i) => i.itemId === item.itemId) : null;
  const opacity = isDragging ? 0.5 : 1;

  return (
    <div ref={ref} className={styles.inventorySlot}>
      {item ? (
        <div
          ref={drag}
          className={`${styles.inventoryItem} ${itemData ? styles[itemData.rarity] : ''}`}
          style={{ opacity }}
          onMouseEnter={(e) =>
            setHoveredItem({
              itemData,
              position: {
                top: e.currentTarget.getBoundingClientRect().top,
                left: e.currentTarget.getBoundingClientRect().left,
              },
            })
          }
          onMouseLeave={() => setHoveredItem(null)}
          onClick={() => setSelectedItem(selectedItem === item ? null : item)}
        >
          <img
            src={`http://localhost:5000/assets/images/items/${itemData?.image}`}
            alt={itemData?.name}
            className={styles.itemImage}
          />
          <p>{itemData?.name} (x{item?.quantity})</p>
        </div>
      ) : (
        <p>Empty Slot</p>
      )}
    </div>
  );
}

export default Inventory;
