// /components/combat/combat.js
import React, { useState } from 'react';

const Combat = ({ enemy, onCombatEnd }) => {
    const [currentEnemy, setCurrentEnemy] = useState(enemy);
    const [playerHp, setPlayerHp] = useState(100);

    const attackEnemy = () => {
        if (!currentEnemy) return;

        // Basic attack logic
        let updatedEnemyHp = currentEnemy.health - Math.floor(Math.random() * 10); // Roll attack between 0 and 10
        if (updatedEnemyHp <= 0) {
            // Enemy defeated
            alert(`${currentEnemy.type} has been defeated!`);
            onCombatEnd();
        } else {
            setCurrentEnemy({ ...currentEnemy, health: updatedEnemyHp });
        }
    };

    const enemyAttack = () => {
        if (!currentEnemy) return;

        // Enemy attacks player
        const damage = Math.floor(Math.random() * currentEnemy.maxHit); // Enemy rolls attack between 0 and maxHit
        setPlayerHp(playerHp - damage);
        if (playerHp <= 0) {
            alert('You have been defeated!');
            onCombatEnd();
        }
    };

    return (
        <div className="combat-container">
            {currentEnemy ? (
                <div className="enemy-details">
                    <h3>Fighting: {currentEnemy.type}</h3>
                    <p>Enemy Health: {currentEnemy.health}</p>
                    <button onClick={attackEnemy}>Attack</button>
                </div>
            ) : (
                <p>No more enemies!</p>
            )}
            <div className="player-details">
                <p>Player HP: {playerHp}</p>
            </div>
        </div>
    );
};

export default Combat;
