// /src/components/users/Login.js
import React, { useState } from 'react';
import achievementData from '../achievements/achievements-list.json'; // Import achievements
import buildingStats from '../buildings/building-stats.json'; // Import building stats

function Login({ setUserId, setGameState }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isRegistered, setIsRegistered] = useState(true); // Toggles between login and register

  const handleLogin = async () => {
    try {
      // Clear game state before attempting to login to prevent data carryover
      if (setGameState) {
        setGameState({
          currency: 0,
          buildings: {},
          achievements: {},
          last_active: null,
          clicks: 0,
          currentLocation: "spawn",
          travel: null,
        });
      } else {
        console.error('setGameState is not a function');
      }

      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        setUserId(data.userId);
        alert('Login successful');
        console.log('Login successful');
        
        // Attempt to load the game save automatically after login
        try {
          const loadResponse = await fetch(`http://localhost:5000/api/load-game/${data.userId}`);
          if (loadResponse.ok) {
            const gameData = await loadResponse.json();
            if (setGameState) {
              setGameState((prevState) => ({
                ...prevState,
                ...gameData.gameState,
              }));
              console.log('Game state loaded successfully:', gameData.gameState);
            }
          } else {
            console.error('No saved game found to load. Player can manually load later.');
          }
        } catch (loadError) {
          console.error('Failed to load game state after login:', loadError);
        }
      } else {
        alert('Login failed: Invalid credentials');
        console.error('Login failed');
      }
    } catch (error) {
      alert('Failed to log in. Please try again later.');
      console.error('Failed to log in:', error);
    }
  };

  const handleRegister = async () => {
    try {
      // Initialize achievements from JSON file
      const initialAchievements = achievementData.reduce((acc, achievement) => {
        acc[achievement.id] = false;
        return acc;
      }, {});

      // Initialize buildings from building-stats.json
      const initialBuildings = Object.keys(buildingStats).reduce((acc, buildingName) => {
        acc[buildingName] = {
          count: 0,
          cost: buildingStats[buildingName].cost,
        };
        return acc;
      }, {});

      const initialGameState = {
        currency: 0,
        buildings: initialBuildings, // Use the buildings loaded from JSON
        achievements: initialAchievements, // Use the achievements loaded from JSON
        last_active: new Date().toISOString(),
        clicks: 0,
        currentLocation: "spawn",
        travel: null,
      };

      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password, gameState: initialGameState }),
      });

      if (response.ok) {
        alert('Registration successful');
        console.log('Registration successful');
        setIsRegistered(true);
      } else if (response.status === 409) {
        alert('Email already exists. Please choose a different email.');
      } else {
        alert('Registration failed. Please try again.');
        console.error('Registration failed');
      }
    } catch (error) {
      alert('Failed to register. Please try again later.');
      console.error('Failed to register:', error);
    }
  };

  return (
    <div className="login-container">
      <h2>{isRegistered ? "Login" : "Register"}</h2>
      <input
        type="text"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={isRegistered ? handleLogin : handleRegister}>
        {isRegistered ? "Login" : "Register"}
      </button>
      <button onClick={() => setIsRegistered(!isRegistered)}>
        {isRegistered ? "Need to register?" : "Already have an account?"}
      </button>
    </div>
  );
}

export default Login;
