import React, { useEffect, useState } from 'react';
import styles from './travel.module.css';

function Travel({ travelInfo, setGameState }) {
  const [timeRemaining, setTimeRemaining] = useState(travelInfo.timeRemaining);

  useEffect(() => {
    if (travelInfo.isTraveling && timeRemaining > 0) {
      console.log(`Begin walking to ${travelInfo.destination}`);
      const timer = setInterval(() => {
        setTimeRemaining((prevTime) => prevTime - 1);
      }, 1000);
      
      // Clear the interval once the timer reaches zero
      if (timeRemaining === 0) {
        clearInterval(timer);
        // Update the game state when travel is complete
        const newState = {
          ...travelInfo,
          currentLocation: travelInfo.destination,
          isTraveling: false,
          timeRemaining: 0,
        };
        console.log(`Travel complete. Arrived at ${travelInfo.destination}`);
        setGameState((prevState) => ({
          ...prevState,
          currentLocation: travelInfo.destination,
          travel: newState,
        }));
      }
      return () => clearInterval(timer);
    }
  }, [timeRemaining, travelInfo, setGameState]);

  // Function to cancel travel
  const cancelTravel = () => {
    console.log('Travel has been canceled');
    setTimeRemaining(0);
    setGameState((prevState) => {
      const updatedState = {
        ...prevState,
        travel: {
          ...travelInfo,
          isTraveling: false,
          timeRemaining: 0,
        },
      };
      console.log("Current location after cancel: ", prevState.currentLocation);
      return updatedState;
    });
  };

  // Prevent traveling to the current location
  if (travelInfo.destination === travelInfo.currentLocation) {
    console.log("You are already at this location.");
    return null;
  }

  return (
    <div className={styles.travelContainer}>
      {travelInfo.isTraveling ? (
        <>
          <h3>Traveling to {travelInfo.destination}</h3>
          <p>Time Remaining: {timeRemaining} seconds</p>
          <div className={styles.progressBar}>
            <div
              className={styles.progress}
              style={{ width: `${((travelInfo.timeRemaining - timeRemaining) / travelInfo.timeRemaining) * 100}%` }}
            />
          </div>
          <button onClick={cancelTravel} className={styles.cancelButton}>Cancel Travel</button>
        </>
      ) : (
        <p>You have arrived at {travelInfo.destination}!</p>
      )}
    </div>
  );
}

export default Travel;
