// /src/components/enemy/enemy.js
import React, { useState, useEffect } from 'react';
import enemyStats from './enemy-stats.json';
import styles from './enemy.module.css';
import worldLocations from '../worldmap/worldLocations.json';

function Enemy({ playerLevel, playerItems, currentLocation, onDefeat }) {
  const [currentEnemy, setCurrentEnemy] = useState(null);
  const [enemyHealth, setEnemyHealth] = useState(0);
  const [playerAttackPower, setPlayerAttackPower] = useState(10); // Placeholder for player's attack power
  const [isRespawning, setIsRespawning] = useState(false);
  const [respawnTimer, setRespawnTimer] = useState(0);

  // Function to spawn an enemy based on player level, items, and location
  const spawnEnemy = () => {
    const availableEnemies = enemyStats.filter((enemy) => {
      if (
        (!enemy.spawnConditions || !enemy.spawnConditions.requiredLevel || playerLevel >= enemy.spawnConditions.requiredLevel) &&
        (!enemy.spawnConditions || !enemy.spawnConditions.requiredItem || playerItems.includes(enemy.spawnConditions.requiredItem)) &&
        (!enemy.location || enemy.location === currentLocation)
      ) {
        return true;
      }
      return false;
    });

    if (availableEnemies.length > 0) {
      let spawnedEnemy = availableEnemies[Math.floor(Math.random() * availableEnemies.length)];

      // Roll for variant chance
      if (spawnedEnemy.variantChance && Math.random() < spawnedEnemy.variantChance) {
        const variantEnemies = availableEnemies.filter(enemy => enemy.baseType === spawnedEnemy.baseType && enemy.variant);
        if (variantEnemies.length > 0) {
          spawnedEnemy = variantEnemies[Math.floor(Math.random() * variantEnemies.length)];
        }
      }

      setCurrentEnemy(spawnedEnemy);
      setEnemyHealth(spawnedEnemy.health);
    }
  };

  useEffect(() => {
    console.log("Current Location ID:", currentLocation); // Check current location value
    console.log("Available Enemies:", enemyStats);
    spawnEnemy();
  }, [playerLevel, playerItems, currentLocation]);      

  // Handle player attack on enemy
  const handleAttack = () => {
    if (currentEnemy) {
      const hitSuccess = Math.random() < playerAttackPower / 100; // Simplified hit chance
      if (hitSuccess) {
        const damage = Math.floor(Math.random() * playerAttackPower);
        setEnemyHealth((prevHealth) => Math.max(prevHealth - damage, 0));
      }
      if (enemyHealth - playerAttackPower <= 0) {
        onDefeat(currentEnemy);
        setIsRespawning(true); // Start respawn process
        setRespawnTimer(currentEnemy.respawnTime); // Set the respawn timer based on enemy data
        setCurrentEnemy(null); // Reset enemy state after defeat
      }
    }
  };

  // Handle enemy's behavior logic
  useEffect(() => {
    if (currentEnemy && enemyHealth > 0) {
      const interval = setInterval(() => {
        if (currentEnemy.behavior === 'aggressive' && Math.random() < currentEnemy.attackChance) {
          console.log(`${currentEnemy.enemyName} attacks!`);
          // Here we can add logic to deal damage to the player, handle defense, etc.
        }
      }, 2000); // Enemy action every 2 seconds
      return () => clearInterval(interval);
    }
  }, [currentEnemy, enemyHealth]);

  // Handle respawn timer countdown
  useEffect(() => {
    if (isRespawning && respawnTimer > 0) {
      const timerInterval = setInterval(() => {
        setRespawnTimer((prevTime) => prevTime - 1);
      }, 1000);
      if (respawnTimer === 0) {
        setIsRespawning(false);
        // Respawn the enemy
        spawnEnemy();
      }
      return () => clearInterval(timerInterval);
    }
  }, [isRespawning, respawnTimer, currentLocation]);

  if (isRespawning) {
    return <div className={styles.enemyContainer}>Respawning in {Math.floor(respawnTimer / 60)}m {respawnTimer % 60}s</div>;
  }

  if (!currentEnemy) {
    return <div className={styles.enemyContainer}>No enemies available</div>;
  }

  return (
    <div className={styles.enemyContainer}>
      <h3>{currentEnemy.enemyName}</h3>
      <img src={require(`../../assets/images/enemies/${currentEnemy.sprite}`)} alt={currentEnemy.enemyName} className={styles.enemySprite} />
      <p>Level: {currentEnemy.level}</p>
      <p>Health: {enemyHealth} / {currentEnemy.health}</p>
      <button onClick={handleAttack} className={styles.attackButton}>Attack</button>
    </div>
  );
}

export default Enemy;
