import React, { useState } from 'react';
import Inventory from '../inventory/inventory';
import styles from './ui.module.css'; // Assuming this CSS module exists

function UI({ gameState, setGameState }) {
  const [isInventoryOpen, setInventoryOpen] = useState(false);

  const toggleInventory = () => {
    setInventoryOpen(!isInventoryOpen);
  };

  return (
    <div className={styles.uiContainer}>
      <button className={styles.inventoryButton} onClick={toggleInventory}>
        Inventory
      </button>

      {isInventoryOpen && (
        <Inventory
          inventory={gameState.inventory}
          setPlayer={setGameState}
          maxInventorySpace={gameState.maxInventorySpace}
        />
      )}
    </div>
  );
}

export default UI;
