// locationDetails.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MagicShop from '../locationFeatures/draynormansion/magicShop'; // Example import for Magic Shop
import styles from './locationDetails.module.css';

function LocationDetails({ currentLocation }) {
  const [selectedFeature, setSelectedFeature] = useState(null);
  const [shopData, setShopData] = useState(null);

  useEffect(() => {
    if (currentLocation && currentLocation.name === 'Draynor Mansion') {
      // Fetch shop data for Magic Shop if the current location is Draynor Mansion
      axios.get('localhost:5001/api/shops/1') // Assuming shop ID 1 is the Magic Shop
        .then((response) => {
          setShopData(response.data);
        })
        .catch((error) => {
          console.error('Failed to fetch shop data:', error);
        });
    }
  }, [currentLocation]);

  if (!currentLocation) {
    return null;
  }

  const { name, description, features = [] } = currentLocation;

  const handleFeatureClick = (feature) => {
    // Toggle the selected feature
    setSelectedFeature((prevFeature) => (prevFeature === feature ? null : feature));
  };

  const renderFeatureComponent = () => {
    switch (selectedFeature) {
      case 'Magic Shop':
        return <MagicShop isOpen={true} shopData={shopData} />;
      default:
        return null;
    }
  };

  return (
    <div className={styles.locationDetailsContainer}>
      <h2>{name}</h2>
      <p>{description}</p>
      <div className={styles.featuresContainer}>
        <h3>Available Features:</h3>
        <ul>
          {features.length > 0 ? (
            features.map((feature, index) => (
              <li key={index} onClick={() => handleFeatureClick(feature)} className={styles.featureItem}>
                {feature}
              </li>
            ))
          ) : (
            <li>No features available</li>
          )}
        </ul>
      </div>
      <div className={styles.featureDetailsContainer}>
        {renderFeatureComponent()}
      </div>
    </div>
  );
}

export default LocationDetails;
