// /src/components/currency/currency.js
import React, { useState, useEffect } from 'react';
import buildingStats from '../buildings/building-stats.json';
import styles from './currency.module.css';
import currency1 from '../../assets/images/currency_1.png';
import currency2 from '../../assets/images/currency_2.png';
import currency3 from '../../assets/images/currency_3.png';
import currency4 from '../../assets/images/currency_4.png';
import currency5 from '../../assets/images/currency_5.png';
import currency6 from '../../assets/images/currency_6.png';
import currency7 from '../../assets/images/currency_7.png';
import currency8 from '../../assets/images/currency_8.png';

function Currency({ gameState, setGameState = () => {} }) {
  const [currency, setCurrency] = useState(0);
  const [totalIncome, setTotalIncome] = useState(0);

  // Load currency from gameState when component mounts
  useEffect(() => {
    if (gameState && gameState.currency !== undefined) {
      setCurrency(gameState.currency);
    }
  }, [gameState.currency]);

  // Calculate total income whenever buildings change
  useEffect(() => {
    if (gameState && gameState.buildings) {
      let income = 0;
      for (const [buildingName, { count }] of Object.entries(gameState.buildings)) {
        if (count > 0 && buildingStats[buildingName]) {
          const buildingIncome = buildingStats[buildingName].income;
          income += buildingIncome * count;
        }
      }
      setTotalIncome(income);
    }
  }, [gameState.buildings]);

  // Update gameState whenever currency changes
  useEffect(() => {
    if (typeof setGameState === 'function') {
      setGameState(prevState => ({
        ...prevState,
        currency: currency
      }));
    }
  }, [currency, setGameState]);

  // Function to increment currency
  const incrementCurrency = () => {
    setCurrency(prevCurrency => prevCurrency + 1);
  };

  // Function to clear currency
  const clearCurrency = () => {
    console.log('Clearing currency for testing purposes');
    setCurrency(0);
  };

  // Determine which currency image to display based on the amount of currency
  const getCurrencyImage = () => {
    if (currency >= 5000) return currency8;
    if (currency >= 1500) return currency7;
    if (currency >= 500) return currency6;
    if (currency >= 250) return currency5;
    if (currency >= 100) return currency4;
    if (currency >= 50) return currency3;
    if (currency >= 20) return currency2;
    return currency1;
  };

  return (
    <div className={styles.currencyContainer}>
      <img src={getCurrencyImage()} alt="Currency Icon" className={styles.currencyIcon} />
      <div>
        <h3>Currency: {currency}</h3>
        <h4>Total Income per Second: {totalIncome}</h4>
      </div>
      <button onClick={incrementCurrency}>Earn Currency</button>
      <button onClick={clearCurrency}>Clear Currency (Dev Button)</button>
    </div>
  );
}

export default Currency;
