import React, { useEffect, useState } from 'react';
import styles from './travel.module.css';

function Travel({ travelInfo, setGameState }) {
  const [timeRemaining, setTimeRemaining] = useState(travelInfo.timeRemaining);

  useEffect(() => {
    if (travelInfo.isTraveling && timeRemaining > 0) {
      const timer = setInterval(() => {
        setTimeRemaining((prevTime) => prevTime - 1);
      }, 1000);
      
      // Clear the interval once the timer reaches zero
      if (timeRemaining === 0) {
        clearInterval(timer);
        // Update the game state when travel is complete
        setGameState((prevState) => ({
          ...prevState,
          currentLocation: travelInfo.destination,
          travel: {
            ...travelInfo,
            isTraveling: false,
            timeRemaining: 0,
          },
        }));
      }

      return () => clearInterval(timer);
    }
  }, [timeRemaining, travelInfo, setGameState]);

  return (
    <div className={styles.travelContainer}>
      {travelInfo.isTraveling ? (
        <>
          <h3>Traveling to {travelInfo.destination}</h3>
          <p>Time Remaining: {timeRemaining} seconds</p>
          <div className={styles.progressBar}>
            <div
              className={styles.progress}
              style={{ width: `${((travelInfo.timeRemaining - timeRemaining) / travelInfo.timeRemaining) * 100}%` }}
            />
          </div>
        </>
      ) : (
        <p>You have arrived at {travelInfo.destination}!</p>
      )}
    </div>
  );
}

export default Travel;
