import React, { useState, useEffect } from 'react';
import styles from './locationDetails.module.css';
import featureRegistry from '../locationFeatures/featureRegistry';
import features from '../worldmap/features.json';
import axios from 'axios';

function LocationDetails({ currentLocation }) {
  const [selectedFeature, setSelectedFeature] = useState(null);
  const [shopData, setShopData] = useState(null);
  const [inventory, setInventory] = useState([]);

  if (!currentLocation) {
    return null;
  }

  const { name, description, feature_ids = [] } = currentLocation;

  // Get the feature details based on feature_ids
  const availableFeatures = features.filter(feature => feature_ids.includes(feature.id));

  const handleFeatureClick = (feature) => {
    setSelectedFeature((prevFeature) => (prevFeature === feature ? null : feature));
  };

  // Fetch shop data whenever the selected feature changes
  useEffect(() => {
    const fetchShopDetails = async () => {
      if (selectedFeature && selectedFeature.component === "MagicShop") {
        try {
          const shopResponse = await axios.get(`http://localhost:5000/api/shops/${selectedFeature.shopId}`);
          setShopData(shopResponse.data);

          const inventoryResponse = await axios.get(`http://localhost:5000/api/shops/${selectedFeature.shopId}/stock`);
          setInventory(inventoryResponse.data);
        } catch (error) {
          console.error("Failed to fetch shop details:", error);
        }
      } else {
        setShopData(null);
        setInventory([]);
      }
    };

    fetchShopDetails();
  }, [selectedFeature]);

  const renderFeatureComponent = () => {
    if (selectedFeature) {
      const FeatureComponent = featureRegistry[selectedFeature.component];
      return FeatureComponent ? (
        <FeatureComponent
          isOpen={true}
          shopId={selectedFeature.shopId}
          shopData={shopData}
          inventory={inventory}
        />
      ) : null;
    }
    return null;
  };

  return (
    <div className={styles.locationDetailsContainer}>
      <h2>{name}</h2>
      <p>{description}</p>
      <div className={styles.featuresContainer}>
        <h3>Available Features:</h3>
        <ul>
          {availableFeatures.length > 0 ? (
            availableFeatures.map((feature, index) => (
              <li
                key={index}
                onClick={() => handleFeatureClick(feature)}
                className={styles.featureItem}
              >
                {feature.name}
              </li>
            ))
          ) : (
            <li>No features available</li>
          )}
        </ul>
      </div>
      <div className={styles.featureDetailsContainer}>
        {renderFeatureComponent()}
      </div>
    </div>
  );
}

export default LocationDetails;
