import React, { useEffect, useState } from 'react';
import styles from './travel.module.css';

function Travel({ travelInfo, setGameState }) {
  const [timeRemaining, setTimeRemaining] = useState(travelInfo?.timeRemaining || 0);

  useEffect(() => {
    let timer;
    if (travelInfo && travelInfo.isTraveling && timeRemaining > 0) {
      console.log('Begin walking to', travelInfo.destination);
      timer = setInterval(() => {
        setTimeRemaining((prevTime) => {
          if (prevTime > 1) {
            return prevTime - 1;
          } else {
            clearInterval(timer);
            console.log('Arrived at', travelInfo.destination);
            // Update the game state when travel is complete
            setGameState((prevState) => ({
              ...prevState,
              currentLocation: travelInfo.destination,
              travel: {
                ...travelInfo,
                isTraveling: false,
                timeRemaining: 0,
              },
            }));
            return 0;
          }
        });
      }, 1000);
    }

    return () => clearInterval(timer);
  }, [timeRemaining, travelInfo, setGameState]);

  // Function to cancel travel
  const cancelTravel = () => {
    console.log('Travel has been canceled');
    setTimeRemaining(0);
    setGameState((prevState) => {
      console.log('Current location after cancel:', prevState.currentLocation);
      return {
        ...prevState,
        travel: {
          ...travelInfo,
          isTraveling: false,
          timeRemaining: 0,
          destination: prevState.currentLocation, // Restore the current location on cancel
        },
      };
    });
  };

  return (
    <div className={styles.travelContainer}>
      {travelInfo?.isTraveling && timeRemaining > 0 ? (
        <>
          <h3>Traveling to {travelInfo?.destination ? travelInfo.destination : travelInfo.id}</h3>
          <p>Time Remaining: {timeRemaining} seconds</p>
          <div className={styles.progressBar}>
            <div
              className={styles.progress}
              style={{ width: `${((travelInfo.timeRemaining - timeRemaining) / travelInfo.timeRemaining) * 100}%` }}
            />
          </div>
          <button onClick={cancelTravel} className={styles.cancelButton}>Cancel Travel</button>
        </>
      ) : (
        travelInfo?.isTraveling === false && travelInfo?.destination ? (
          <p>You have arrived at {travelInfo.destination}!</p>
        ) : (
          <p>Not currently traveling.</p>
        )
      )}
    </div>
  );
}

export default Travel;
