// /components/worldmap/worldMap.js
import React, { useState } from 'react';
import styles from './worldMap.module.css';
import worldLocations from './worldLocations.json';
import mapIcon from '../../assets/images/icons/map.png';
import worldMapImage from '../../assets/images/worldmap/worldmap.png';

function WorldMap({ gameState, setGameState }) {
  const [isMapOpen, setIsMapOpen] = useState(false);
  const [hoveredLocation, setHoveredLocation] = useState(null);

  // Toggle map visibility
  const toggleMap = () => {
    setIsMapOpen((prev) => !prev);
  };

  // Handle traveling to a specific location
  const travelToLocation = (location) => {
    // Set travel state in gameState
    setGameState((prevState) => ({
      ...prevState,
      travel: {
        destination: location.name,
        timeRemaining: location.travelTime,
        isTraveling: true,
      },
      currentLocation: location.id, // Update current location in gameState
    }));
    setIsMapOpen(false); // Close the map after choosing a destination
  };

  // Handle mouse hover over a location
  const handleMouseEnter = (location) => {
    setHoveredLocation(location);
  };

  const handleMouseLeave = () => {
    setHoveredLocation(null);
  };

  return (
    <div className={styles.worldMapContainer}>
      <button onClick={toggleMap} className={styles.mapButton}>
        <img src={mapIcon} alt="Map" className={styles.mapIcon} />
      </button>

      {isMapOpen && (
        <div className={styles.mapModal}>
          <img src={worldMapImage} alt="World Map" className={styles.mapImage} />
          <div className={styles.locationsContainer}>
            {worldLocations.map((location) => (
              <div key={location.id}>
                {/* Location Marker */}
                <div
                  className={styles.locationMarker}
                  style={{
                    top: location.coordinates.top,
                    left: location.coordinates.left,
                  }}
                  onClick={() => travelToLocation(location)}
                  onMouseEnter={() => handleMouseEnter(location)}
                  onMouseLeave={handleMouseLeave}
                />
                
                {/* Location Name on Hover */}
                {hoveredLocation && hoveredLocation.id === location.id && (
                  <div
                    className={styles.locationNameTooltip}
                    style={{
                      top: `calc(${location.coordinates.top} - 20px)`,
                      left: location.coordinates.left,
                    }}
                  >
                    {location.name}
                  </div>
                )}
              </div>
            ))}
          </div>
          <button onClick={toggleMap} className={styles.closeButton}>Close Map</button>
        </div>
      )}
    </div>
  );
}

export default WorldMap;
