const Combat = ({ enemies }) => {
    const [currentEnemy, setCurrentEnemy] = useState(enemies.length ? enemies[0] : null);
    const [playerHp, setPlayerHp] = useState(100);

    const attackEnemy = () => {
        if (!currentEnemy) return;

        // Basic attack logic
        let updatedEnemyHp = currentEnemy.hp - 10;
        if (updatedEnemyHp <= 0) {
            // Enemy defeated
            const remainingEnemies = enemies.filter(enemy => enemy !== currentEnemy);
            setCurrentEnemy(remainingEnemies.length ? remainingEnemies[0] : null);
        } else {
            setCurrentEnemy({ ...currentEnemy, hp: updatedEnemyHp });
        }
    };

    const enemyAttack = () => {
        if (!currentEnemy) return;

        // Enemy attacks player
        setPlayerHp(playerHp - currentEnemy.damage);
        if (playerHp <= 0) {
            alert('You have been defeated!');
        }
    };

    return (
        <div className="combat-container">
            {currentEnemy ? (
                <div className="enemy-details">
                    <h3>Fighting: {currentEnemy.type}</h3>
                    <p>Enemy HP: {currentEnemy.hp}</p>
                    <button onClick={attackEnemy}>Attack</button>
                </div>
            ) : (
                <p>No more enemies!</p>
            )}
            <div className="player-details">
                <p>Player HP: {playerHp}</p>
            </div>
        </div>
    );
};

export default Combat;
