// /src/components/gamesaves/gameSaves.js
import React from 'react';

function GameSaves({ gameState, setGameState }) {
  const userId = "user123"; // Placeholder for user ID

  // Save the current game state to the server
  const saveGameToServer = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/save-game', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId,
          gameState,
        }),
      });
      const data = await response.json();
      console.log(data.message);
    } catch (error) {
      console.error('Failed to save game to server:', error);
    }
  };

  // Load the game state from the server
  const loadGameFromServer = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/load-game/${userId}`);
      if (response.ok) {
        const data = await response.json();
        setGameState(JSON.parse(data.gameState));
        console.log('Game state loaded successfully:', data.gameState);
      } else {
        console.error('No saved game found.');
      }
    } catch (error) {
      console.error('Failed to load game from server:', error);
    }
  };

  // Export the current game state as a JSON file
  const exportGameState = () => {
    const jsonString = JSON.stringify(gameState, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'gameState.json';
    link.click();
  };

  // Import a saved game state from a JSON file
  const importGameState = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const content = e.target.result;
      const importedGameState = JSON.parse(content);
      setGameState(importedGameState);

      // Optionally, save imported game state to server
      saveGameToServer();
    };
    reader.readAsText(file);
  };

  return (
    <div className="game-saves">
      <h2>Game Saves</h2>
      <button onClick={saveGameToServer}>Save Game to Server</button>
      <button onClick={loadGameFromServer}>Load Game from Server</button>
      <button onClick={exportGameState}>Export Game State</button>
      <input
        type="file"
        accept="application/json"
        onChange={importGameState}
      />
    </div>
  );
}

export default GameSaves;
