import React, { useEffect, useState } from 'react';
import styles from './combat.module.css';

function Combat({ currentEnemy, playerAttackPower, enemyHealth, setEnemyHealth, onEndCombat, onDefeat }) {
  const [playerHealth, setPlayerHealth] = useState(100); // Example player's health
  const [combatLog, setCombatLog] = useState([]); // To log actions during combat

  // Player attack
  const handlePlayerAttack = () => {
    const hitSuccess = Math.random() < playerAttackPower / 100; // Simplified hit chance
    if (hitSuccess) {
      const damage = Math.floor(Math.random() * playerAttackPower);
      setEnemyHealth((prevHealth) => Math.max(prevHealth - damage, 0));
      addCombatLog(`You hit ${currentEnemy.enemyName} for ${damage} damage!`);
    } else {
      addCombatLog("You missed!");
    }
  };

  // Enemy attack back
  useEffect(() => {
    if (currentEnemy && enemyHealth > 0) {
      const enemyAttackInterval = setInterval(() => {
        if (Math.random() < currentEnemy.attackChance) {
          const damage = Math.floor(Math.random() * currentEnemy.maxHit);
          setPlayerHealth((prevHealth) => Math.max(prevHealth - damage, 0));
          addCombatLog(`${currentEnemy.enemyName} hit you for ${damage} damage!`);
        } else {
          addCombatLog(`${currentEnemy.enemyName} missed!`);
        }
      }, 2000);
      return () => clearInterval(enemyAttackInterval);
    }
  }, [currentEnemy, enemyHealth]);

  // Handle combat end if either the player or enemy health reaches zero
  useEffect(() => {
    if (enemyHealth <= 0) {
      addCombatLog(`You defeated ${currentEnemy.enemyName}!`);
      onDefeat(currentEnemy);
      onEndCombat(playerHealth); // Update player health when ending combat
    } else if (playerHealth <= 0) {
      addCombatLog("You have been defeated...");
      onEndCombat(playerHealth); // Update player health when defeated
    }
  }, [enemyHealth, playerHealth, currentEnemy, onDefeat, onEndCombat]);

  // Add an entry to the combat log
  const addCombatLog = (message) => {
    setCombatLog((prevLog) => [...prevLog, message]);
  };

  // Handle fleeing
  const handleFlee = () => {
    addCombatLog("You fled the battle!");
    onEndCombat(playerHealth); // Ensure player's current health is saved when fleeing
  };

  return (
    <div className={styles.combatContainer}>
      <h3>Combat with {currentEnemy.enemyName}</h3>
      <p>Enemy Health: {enemyHealth} / {currentEnemy.health}</p>
      <p>Your Health: {playerHealth} / 100</p>
      <div className={styles.combatLog}>
        {combatLog.map((log, index) => (
          <p key={index}>{log}</p>
        ))}
      </div>
      <button onClick={handlePlayerAttack} className={styles.attackButton}>Attack</button>
      <button onClick={handleFlee} className={styles.fleeButton}>Flee</button>
    </div>
  );
}

export default Combat;
