// magicShop.js
import React, { useState, useEffect } from 'react';
import styles from './magicShop.module.css';
import shopData from './magicShop.json';

function MagicShop({ isOpen }) {
  const [inventory, setInventory] = useState([]);

  useEffect(() => {
    // Load the inventory items from the JSON file
    setInventory(shopData.inventory);
  }, []);

  if (!isOpen) {
    return null;
  }

  const getItemImage = (item) => {
    try {
      return require(`../../../assets/images/items/${item.image}.png`);
    } catch (error) {
      return require(`../../../assets/images/items/item_dwarfremains.png`);
    }
  };

  return (
    <div className={styles.magicShopContainer}>
      <h2>{shopData.shopName}</h2>
      <div className={styles.shopOwnerContainer}>
        <img src={require(`../../../assets/images/npcs/${shopData.shopOwner.image}`)} alt={shopData.shopOwner.name} className={styles.shopOwnerImage} />
        <p>Shop Owner: {shopData.shopOwner.name}</p>
      </div>
      <div className={styles.inventoryContainer}>
        {inventory.length > 0 ? (
          <div className={styles.itemList}>
            {inventory.map((item, index) => (
              <li key={index} className={styles.item}>
                <img src={getItemImage(item)} alt={item.name} className={styles.itemImage} />
                <h4>{item.name}</h4>
                <p>Price: {item.price} gold</p>
                <p>Quantity: {item.quantity}</p>
                <button className={styles.buyButton}>Buy</button>
              </li>
            ))}
          </div>) : (
          <p>No items available for sale.</p>
        )}
      </div>
    </div>
  );
}

export default MagicShop;
