import React, { useState, useEffect } from 'react';
import styles from './worldMap.module.css';
import worldLocations from './worldLocations.json';
import mapIcon from '../../assets/images/icons/map.png';
import worldMapImage from '../../assets/images/worldmap/worldmap.png';

function WorldMap({ gameState, setGameState }) {
  const [isMapOpen, setIsMapOpen] = useState(false);
  const [hoveredLocation, setHoveredLocation] = useState(null);
  const [travelTimer, setTravelTimer] = useState(null);

  useEffect(() => {
    console.log("Initial Game State: ", gameState);
  }, []);

  // Toggle map visibility
  const toggleMap = () => {
    setIsMapOpen((prev) => !prev);
    console.log("Map toggled. Is map open: ", !isMapOpen);
  };

  // Handle traveling to a specific location
  const travelToLocation = (location) => {
    // Prevent traveling to the current location
    if (gameState.currentLocation === location.id) {
      console.log("You are already at this location.");
      return;
    }

    console.log(`Begin traveling to ${location.name}`);

    // Set travel state in gameState
    setGameState((prevState) => {
      const newGameState = {
        ...prevState,
        travel: {
          currentLocation: prevState.currentLocation,
          destination: location.name,
          timeRemaining: location.travelTime,
          isTraveling: true,
        },
      };
      console.log("Updated Game State (Start Travel): ", newGameState);
      return newGameState;
    });
    setIsMapOpen(false); // Close the map after choosing a destination

    // Start the travel timer
    if (travelTimer) {
      clearInterval(travelTimer);
    }
    const timer = setInterval(() => {
      setGameState((prevState) => {
        if (prevState.travel.timeRemaining > 1) {
          const updatedGameState = {
            ...prevState,
            travel: {
              ...prevState.travel,
              timeRemaining: prevState.travel.timeRemaining - 1,
            },
          };
          console.log("Updated Game State (During Travel): ", updatedGameState);
          return updatedGameState;
        } else {
          clearInterval(timer);
          setTravelTimer(null); // Clear the travel timer state
          // Update gameState once travel is complete
          const completedGameState = {
            ...prevState,
            currentLocation: prevState.travel.destination,
            travel: {
              currentLocation: prevState.travel.destination,
              destination: '',
              timeRemaining: 0,
              isTraveling: false,
            },
          };
          console.log("Updated Game State (Travel Complete): ", completedGameState);
          return completedGameState;
        }
      });
    }, 1000);
    setTravelTimer(timer);
  };

  useEffect(() => {
    console.log("Updated Game State (Current Location): ", gameState.currentLocation);
  }, [gameState.currentLocation]);

  useEffect(() => {
    console.log("Updated Game State: ", gameState);
  }, [gameState]);

  return (
    <div className={styles.worldMapContainer}>
      <button onClick={toggleMap} className={styles.mapButton}>
        <img src={mapIcon} alt="Map" className={styles.mapIcon} />
      </button>

      {/* Display Current Location */}
      <p className={styles.currentLocation}>Currently at: {gameState.currentLocation}</p>

      {isMapOpen && (
        <div className={styles.mapModal}>
          <img src={worldMapImage} alt="World Map" className={styles.mapImage} />
          <div className={styles.locationsContainer}>
            {worldLocations.map((location) => (
              <div key={location.id}>
                {/* Location Marker */}
                <div
                  className={styles.locationMarker}
                  style={{
                    top: location.coordinates.top,
                    left: location.coordinates.left,
                  }}
                  onClick={() => travelToLocation(location)}
                />
                
                {/* Location Name on Hover */}
                {hoveredLocation && hoveredLocation.id === location.id && (
                  <div
                    className={styles.locationNameTooltip}
                    style={{
                      top: `calc(${location.coordinates.top} - 20px)`,
                      left: location.coordinates.left,
                    }}
                  >
                    {location.name}
                  </div>
                )}
              </div>
            ))}
          </div>
          <button onClick={toggleMap} className={styles.closeButton}>Close Map</button>
        </div>
      )}
    </div>
  );
}

export default WorldMap;
