import React, { useState } from 'react';
import GridLayout from 'react-grid-layout';
import styles from './inventory.module.css';
import itemConfig from '../items/itemConfig.json';

function Inventory({ inventory = [], setPlayer, maxInventorySpace }) {
  const [hoveredItem, setHoveredItem] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);

  // Define layout for all slots, even if they're empty
  const layout = Array.from({ length: maxInventorySpace }, (_, index) => ({
    i: `${index}`,
    x: index % 3,
    y: Math.floor(index / 3),
    w: 1,
    h: 1,
  }));

  // Handle layout change to rearrange items
  const handleLayoutChange = (newLayout) => {
    const reorderedInventory = new Array(maxInventorySpace).fill(null);

    newLayout.forEach((item) => {
      const oldIndex = parseInt(item.i);
      if (inventory[oldIndex]) {
        const newIndex = newLayout.findIndex((slot) => slot.i === item.i);
        reorderedInventory[newIndex] = inventory[oldIndex];
      }
    });

    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      inventory: reorderedInventory.filter((item) => item !== null),
    }));
  };

  return (
    <div className={styles.inventoryContainer}>
      <GridLayout
        className="layout"
        layout={layout}
        cols={3}
        rowHeight={50}
        width={300}
        onLayoutChange={handleLayoutChange}
      >
        {layout.map((slot) => {
          const item = inventory[parseInt(slot.i)];
          if (item) {
            const itemData = itemConfig.items.find((i) => i.itemId === item.itemId);
            if (!itemData) return null;

            const rarityClass = {
              common: styles.common,
              uncommon: styles.uncommon,
              rare: styles.rare,
              'very rare': styles.veryRare,
              mythical: styles.mythical,
              quest: styles.quest,
            };

            return (
              <div key={slot.i} data-grid={slot} className="inventorySlot">
                <div
                  className={`${styles.inventoryItem} ${rarityClass[itemData.rarity]}`}
                  onMouseEnter={(e) =>
                    setHoveredItem({
                      itemData,
                      position: {
                        top: e.currentTarget.getBoundingClientRect().top,
                        left: e.currentTarget.getBoundingClientRect().left,
                      },
                    })
                  }
                  onMouseLeave={() => setHoveredItem(null)}
                  onClick={() => setSelectedItem(selectedItem === item ? null : item)}
                >
                  <img
                    src={`http://localhost:5000/assets/images/items/${itemData.image}`}
                    alt={itemData.name}
                    className={styles.itemImage}
                  />
                  <p>{itemData.name} (x{item.quantity})</p>
                </div>
              </div>
            );
          } else {
            return (
              <div key={slot.i} data-grid={slot} className={styles.emptySlot}>
                <p>Empty Slot</p>
              </div>
            );
          }
        })}
      </GridLayout>
      {hoveredItem && hoveredItem.itemData && (
        <div className={styles.tooltip} style={hoveredItem.position}>
          <p>
            <strong>{hoveredItem.itemData.name}</strong>
          </p>
          <p>{hoveredItem.itemData.description}</p>
          {hoveredItem.itemData.attackBoost !== undefined &&
            hoveredItem.itemData.attackBoost !== 0 && (
              <p>Attack Boost: {hoveredItem.itemData.attackBoost}</p>
            )}
          {hoveredItem.itemData.defenceBoost !== undefined &&
            hoveredItem.itemData.defenceBoost !== 0 && (
              <p>Defence Boost: {hoveredItem.itemData.defenceBoost}</p>
            )}
          {hoveredItem.itemData.effect && hoveredItem.itemData.effect.heal && (
            <p>Heal: {hoveredItem.itemData.effect.heal}</p>
          )}
        </div>
      )}
    </div>
  );
}

export default Inventory;
