import React, { useState } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import styles from './inventory.module.css';
import itemConfig from '../items/itemConfig.json';

const ItemTypes = {
  ITEM: 'item',
};

function Inventory({ inventory = [], setPlayer, maxInventorySpace }) {
  const [hoveredItem, setHoveredItem] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);
  const [itemOptions, setItemOptions] = useState(null);

  // Handle item movement in the inventory
  const handleMoveItem = (fromIndex, toIndex) => {
    if (fromIndex === toIndex) return;

    const updatedInventory = [...inventory];
    const [movedItem] = updatedInventory.splice(fromIndex, 1);
    updatedInventory.splice(toIndex, 0, movedItem);

    // Remove null entries to keep items close together
    const filteredInventory = updatedInventory.filter((item) => item !== null);

    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      inventory: filteredInventory,
    }));
  };

  // Handle left-click to view item options
  const handleLeftClick = (item, index) => {
    if (item) {
      setItemOptions({ item, index });
    } else {
      setItemOptions(null);
    }
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className={styles.inventoryContainer}>
        <div className={styles.layout}>
          {inventory.map((item, index) => (
            <InventorySlot
              key={index}
              index={index}
              item={item}
              moveItem={handleMoveItem}
              selectedItem={selectedItem}
              setSelectedItem={setSelectedItem}
              onLeftClick={handleLeftClick}
            />
          ))}
        </div>
        {itemOptions && (
          <div className={styles.itemOptions}>
            <h4>Item Options</h4>
            <p>{itemOptions.item.name}</p>
            <button onClick={() => console.log(`Use ${itemOptions.item.name}`)}>Use</button>
            <button onClick={() => console.log(`Inspect ${itemOptions.item.name}`)}>Inspect</button>
            <button onClick={() => console.log(`Drop ${itemOptions.item.name}`)}>Drop</button>
          </div>
        )}
      </div>
    </DndProvider>
  );
}

function InventorySlot({ index, item, moveItem, onLeftClick }) {
  const [, dropRef] = useDrop({
    accept: ItemTypes.ITEM,
    hover: (draggedItem, monitor) => {
      if (!monitor.isOver({ shallow: true })) return;
      if (draggedItem.index === index) return;

      // Swap logic
      moveItem(draggedItem.index, index);
      draggedItem.index = index;
    },
  });

  const [{ isDragging }, dragRef] = useDrag({
    type: ItemTypes.ITEM,
    item: () => ({ index }), // Correctly updated to use item()
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const itemData = item ? itemConfig.items.find((i) => i.itemId === item.itemId) : null;
  const opacity = isDragging ? 0.5 : 1;

  return (
    <div
      ref={dropRef}
      className={styles.inventorySlot}
      onClick={() => onLeftClick(item, index)} // Handle left click
    >
      {item ? (
        <div
          ref={dragRef}
          className={`${styles.inventoryItem} ${itemData ? styles[itemData.rarity] : ''}`}
          style={{ opacity }}
        >
          <img
            src={`http://localhost:5000/assets/images/items/${itemData?.image}`}
            alt={itemData?.name}
            className={styles.itemImage}
          />
          <p>{itemData?.name} (x{item?.quantity})</p>
        </div>
      ) : null}
    </div>
  );
}

export default Inventory;
