// /src/components/achievements/achievements.js
import React, { useEffect, useState } from 'react';
import styles from './achievements.module.css';

function Achievements({ gameState, setGameState }) {
  const [achievements, setAchievements] = useState([]);

  // Define basic achievements
  const achievementList = [
    { id: 1, name: 'First Steps', condition: (gs) => gs.currency >= 100, description: 'Earn 100 currency.' },
    { id: 2, name: 'Builder', condition: (gs) => gs.buildings && Object.values(gs.buildings).some((b) => b.count >= 1), description: 'Buy your first building.' },
    { id: 3, name: 'Collector', condition: (gs) => gs.currency >= 1000, description: 'Earn 1,000 currency.' },
    { id: 4, name: 'Real Estate Mogul', condition: (gs) => gs.buildings && Object.values(gs.buildings).reduce((acc, b) => acc + b.count, 0) >= 10, description: 'Own 10 buildings.' },
    { id: 5, name: 'Millionaire', condition: (gs) => gs.currency >= 1000000, description: 'Earn 1,000,000 currency.' },
  ];

  useEffect(() => {
    // Check for completed achievements
    const unlockedAchievements = achievementList.filter((achievement) => achievement.condition(gameState));
    setAchievements(unlockedAchievements);

    // Update gameState with unlocked achievements
    setGameState((prevState) => ({
      ...prevState,
      achievements: unlockedAchievements.map((achievement) => achievement.id),
    }));
  }, [gameState, setGameState]);

  return (
    <div className={styles.achievementsContainer}>
      <h2>Achievements</h2>
      <div className={styles.achievementList}>
        {achievementList.map((achievement) => (
          <div
            key={achievement.id}
            className={
              achievements.some((ach) => ach.id === achievement.id)
                ? `${styles.achievementItem} ${styles.achieved}`
                : styles.achievementItem
            }
          >
            <h3>{achievement.name}</h3>
            <p>{achievement.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Achievements;
