// /components/ui/ui.js
import React, { useState } from 'react';
import styles from './ui.module.css';
import Inventory from '../inventory/inventory';
import Equipment from '../equipment/equipment';

function UI({ inventory, setPlayer, maxInventorySpace, gameState, setGameState }) {
  const [isInventoryOpen, setIsInventoryOpen] = useState(false);
  const [isEquipmentOpen, setIsEquipmentOpen] = useState(false);

  const handleInventoryToggle = () => {
    setIsInventoryOpen(!isInventoryOpen);
  };

  const handleEquipmentToggle = () => {
    setIsEquipmentOpen(!isEquipmentOpen);
  };

  return (
    <div>
      <div className={styles.uiContainer}>
        <button className={styles.uiButton} onClick={handleInventoryToggle}>Inventory</button>
        <button className={styles.uiButton} onClick={handleEquipmentToggle}>Button 2</button>
        <button className={styles.uiButton}>Button 3</button>
      </div>
      {isInventoryOpen && (
        <div className={styles.inventoryOverlay}>
          <Inventory 
            inventory={inventory} 
            setPlayer={setPlayer} 
            maxInventorySpace={maxInventorySpace} 
          />
        </div>
      )}
      {isEquipmentOpen && gameState && setGameState && (
        <div className={styles.inventoryOverlay}>
          <Equipment 
            gameState={gameState} 
            setGameState={setGameState} 
          />
        </div>
      )}
    </div>
  );
}

export default UI;
