// Player.js
import React, { useState, useEffect } from 'react';
import styles from './player.module.css';

function Player({ initialHP = 100, username }) {
  // Define player state including HP, inventory, and bank
  const [player, setPlayer] = useState({
    hp: initialHP,
    maxHP: initialHP,
    inventory: [],
    bank: [],
    maxInventorySpace: 10,
  });

  useEffect(() => {
    if (username) {
      console.log(`Logged in as: ${username}`);
    }
  }, [username]);

  // Function to add an item to inventory
  const addItemToInventory = (item) => {
    if (player.inventory.length < player.maxInventorySpace) {
      setPlayer((prevPlayer) => ({
        ...prevPlayer,
        inventory: [...prevPlayer.inventory, item],
      }));
    } else {
      console.log('Inventory is full. Item has been added to the bank.');
      addItemToBank(item);
    }
  };

  // Function to add an item to the bank
  const addItemToBank = (item) => {
    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      bank: [...prevPlayer.bank, item],
    }));
  };

  // Function to remove an item from inventory and add it to the bank
  const moveItemToBank = (itemIndex) => {
    const item = player.inventory[itemIndex];
    setPlayer((prevPlayer) => {
      const newInventory = [...prevPlayer.inventory];
      newInventory.splice(itemIndex, 1);
      return {
        ...prevPlayer,
        inventory: newInventory,
        bank: [...prevPlayer.bank, item],
      };
    });
  };

  // Function to move an item from the bank to inventory
  const moveItemToInventory = (itemIndex) => {
    if (player.inventory.length < player.maxInventorySpace) {
      const item = player.bank[itemIndex];
      setPlayer((prevPlayer) => {
        const newBank = [...prevPlayer.bank];
        newBank.splice(itemIndex, 1);
        return {
          ...prevPlayer,
          inventory: [...prevPlayer.inventory, item],
          bank: newBank,
        };
      });
    } else {
      console.log('Inventory is full. Cannot retrieve item from bank.');
    }
  };

  return (
    <div className={styles.playerContainer}>
      <h2>Player Stats</h2>
      {username && <p>Username: {username}</p>}
      <p>HP: {player.hp}/{player.maxHP}</p>

      <h3>Inventory ({player.inventory.length}/{player.maxInventorySpace})</h3>
      <ul className={styles.inventoryList}>
        {player.inventory.map((item, index) => (
          <li key={index}>
            {item.name} 
            <button onClick={() => moveItemToBank(index)}>Move to Bank</button>
          </li>
        ))}
      </ul>

      <h3>Bank ({player.bank.length} items)</h3>
      <ul className={styles.bankList}>
        {player.bank.map((item, index) => (
          <li key={index}>
            {item.name} 
            <button onClick={() => moveItemToInventory(index)}>Retrieve</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Player;
