// /src/components/buildings/buildings.js
import React, { useState, useEffect } from 'react';

function Buildings({ gameState, setGameState }) {
  const [buildings, setBuildings] = useState([]);

  useEffect(() => {
    // Load existing buildings from gameState if available
    if (gameState && gameState.buildings) {
      setBuildings(gameState.buildings);
    }
  }, [gameState]);

  // Add a new building
  const addBuilding = (buildingName) => {
    const newBuildings = [...buildings, buildingName];
    setBuildings(newBuildings);

    // Update gameState with new buildings array
    setGameState((prevState) => ({
      ...prevState,
      buildings: newBuildings,
    }));
  };

  return (
    <div className="buildings">
      <h2>Buildings</h2>
      <ul>
        {buildings.map((building, index) => (
          <li key={index}>{building}</li>
        ))}
      </ul>
      <button onClick={() => addBuilding('Cow Pen')}>Add Cow Pen</button>
      <button onClick={() => addBuilding('Wheat Field')}>Add Wheat Field</button>
      <button onClick={() => addBuilding('Varrock')}>Add Varrock</button>
    </div>
  );
}

export default Buildings;
